detailTemplate.body:
{{this.item = model}}
<table class="table table-hover">
  <tbody>
    <tr>
      <td>Ministerstvo
      </td>
      <td>{{ if (fn_IsNullOrEmpty item.ministry.ICO)}}
        {{ fn_NormalizeText item.ministry.name }}{{else}}
        <a href="https://www.hlidacstatu.cz/subjekt/{{item.ministry.ICO}}">{{fn_NormalizeText item.ministry.name}}
        </a>
        <br />{{end #if }}
        </td>
      </tr>
      <tr>
        <td>Faktura
        </td>
        <td>{{item.authorityIdentifier}}
        </td>
      </tr>
      <tr>
        <td>Typ záznamu
        </td>
        <td>{{ if (item.recordType == "invoice")}}
faktura{{else if (item.recordType == "payment")}}
platba{{else if (item.recordType == "transferorder")}}
platební poukaz{{else if (item.recordType == "deposit")}}
zálohová faktura{{else if (item.recordType == "creditnote")}}
dobropis{{else}}
          {{item.recordType}}{{end #if }}
        </td>
      </tr>
      <tr>
        <td>Účel platby
        </td>
        <td>{{ if (fn_IsNullOrEmpty item.subject)}}
neuvedeno{{else}}
          {{ fn_HighlightText highlightingData item.subject "subject" }}{{end #if}}
        </td>
      </tr>
      <tr>
        <td>Název položky rozpočtu
        </td>
        <td>{{ if (fn_IsNullOrEmpty item.budgetCategory)}}
neuvedeno{{else}}
          {{ fn_NormalizeText item.budgetCategory}}{{end #if}}
        </td>
      </tr>
      <tr>
        <td>Variabilní symbol
        </td>
        <td>{{ if (fn_IsNullOrEmpty item.variableSymbol)}}
neuvedeno{{else}}
          {{ fn_NormalizeText item.variableSymbol}}{{end #if}}
        </td>
      </tr>
      <tr>
        <td>Datum přijetí
        </td>
        <td>{{ if (fn_IsNullOrEmpty item.dateCreated)}}
neuvedeno{{ else }}
          {{fn_FormatDate item.dateCreated  "dd.MM.yyyy"}}{{end #if}}
        </td>
      </tr>
      <tr>
        <td>Datum splatnosti
        </td>
        <td>{{ if (fn_IsNullOrEmpty item.dueDate)}}
neuvedeno{{ else }}
          {{fn_FormatDate item.dueDate  "dd.MM.yyyy"}}{{end #if}}
        </td>
      </tr>
      <tr>
        <td>Datum úhrady
        </td>
        <td>{{ if (fn_IsNullOrEmpty item.dateOfPayment)}}
neuvedeno{{ else }}
          {{fn_FormatDate item.dateOfPayment  "dd.MM.yyyy"}}{{end #if}}
        </td>
      </tr>
      <tr>
        <td>Měna
        </td>
        <td>{{ if (fn_IsNullOrEmpty item.currency)}}
neuvedeno{{ else }}
          {{item.currency}}{{end #if}}
        </td>
      </tr>
      <tr>
        <td>Celková částka v původní měně
        </td>
        <td>{{ if (fn_IsNullOrEmpty item.originalCurrencyAmount)}}
neuvedeno{{ else }}
          {{ item.originalCurrencyAmount}}{{end #if}}
        </td>
      </tr>
      <tr>
        <td>Celková částka v CZK
        </td>
        <td>{{ if (fn_IsNullOrEmpty item.amountCzk)}}
neuvedeno{{ else }}
          {{ fn_FormatPrice item.amountCzk}}{{end #if}}
        </td>
      </tr>
      <tr>
        <td>Účastník
        </td>
        <td>{{ if ((fn_IsNullOrEmpty item.partner.ICO) && (fn_IsNullOrEmpty item.partner.name))}}
neuvedeno{{else if (fn_IsNullOrEmpty item.partner.ICO)}}
          {{ fn_NormalizeText item.partner.name }}{{else}}
          <a href="https://www.hlidacstatu.cz/subjekt/{{item.partner.ICO}}">{{fn_NormalizeText item.partner.name}}
          </a>
          <br />{{end #if }}
          </td>
        </tr>
        <tr>
          <td>IČO účastníka
          </td>
          <td>{{ if (fn_IsNullOrEmpty item.partner.ICO)}}
neuvedeno{{else}}
            {{ item.partner.ICO }}{{end #if }}
          </td>
        </tr>
      </tbody>
    </table>

{{this.item = model}}

<h2>{{ item.vec }}</h2>

<table class="table table-hover"><tbody>

<tr><td style="font-weight:bold;">Datum jednání vlády</td><td >
{{ fn_FormatDate item.datum 'dd. MM. yyyy' }}
<br/>
<a href="https://www.hlidacstatu.cz/data/Hledat/jednani-vlady?Q=datum%3A{{ fn_FormatDate item.datum 'yyyy-MM-dd' }}">Všechny projednávané body na tomto jednání vlády</a>
</td></tr>


<tr><td style="font-weight:bold;">Bod jednání</td><td >{{ item.bod }}</a></td></tr>

{{ if !(fn_IsNullOrEmpty item.cisloJednaci)  }}
   <tr><td style="font-weight:bold;">Číslo jednací</td><td >{{ item.cisloJednaci }}</td></tr>
{{end }}

<tr><td style="font-weight:bold;">Obsah projednáváného bodu</td><td >{{ item.vec }}</td></tr>


{{ if item.dokumenty && item.dokumenty.size > 0  }}

   <tr><td style="font-weight:bold;">Projednávané dokumenty</td><td >
<ul>
{{ for doc in item.dokumenty }}

   <li> 
      {{ doc.jmeno }} - 
      {{fn_LinkTextDocumentWithHighlighting doc "jednani-vlady" item.Id "Obsah dokumentu" highlightingData }}
   </li>

{{ end }}
</ul>
<div class="text-muted small">Vláda uveřejňuje dokumenty obvykle ve dvou formátech (DOC a PDF). Pro úplnost zpracováváme obě verze souboru, i když jsou obvykle shodné</div>
</td></tr>


{{end }}

{{ if ((item.rusi.size?? 0 ) + (item.meni.size ?? 0)) > 0  }}

   <tr><td style="font-weight:bold;">Toto usnesení má vliv na jiná usnesení </td><td >
   <ul>
   {{ for d in (array.concat item.meni item.rusi ) }}

     <li> 

      {{ d.zmena }} 
      {{ if fn_IsNullOrEmpty d.usneseniCislo }}
           {{ d.usneseni }}
      {{ else }}
          <a href="https://www.hlidacstatu.cz/data/Hledat/jednani-vlady?Q=cisloJednaci.keyword:{{ string.replace d.usneseniCislo "/" "%2F" }}">
          {{ d.usneseni }}</a>
      {{ end }}
     </li>

  {{ end }}
  </ul>
  </td></tr>
{{end }}

{{ if (item.zruseno.size ) > 0  }}

   <tr><td style="font-weight:bold;">Toto usnesení je zrušeno</td><td >
   <ul>
   {{ for d in item.zruseno }}

     <li> 

      {{ d.zmena }} 
      {{ if fn_IsNullOrEmpty d.usneseniCislo }}
           {{ d.usneseni }}
      {{ else }}
          <a href="https://www.hlidacstatu.cz/data/Hledat/jednani-vlady?Q=cisloJednaci.keyword:{{ string.replace d.usneseniCislo "/" "%2F" }}">
          {{ d.usneseni }}</a>
      {{ end }}
     </li>

  {{ end }}
  </ul>
  </td></tr>
{{ end }}

{{ if (item.zmeneno.size) > 0  }}

   <tr><td style="font-weight:bold;">Toto usnesení bylo změněno</td><td >
   <ul>
   {{ for d in item.zmeneno }}

     <li> 

      {{ d.zmena }} 
      {{ if fn_IsNullOrEmpty d.usneseniCislo }}
           {{ d.usneseni }}
      {{ else }}
          <a href="https://www.hlidacstatu.cz/data/Hledat/jednani-vlady?Q=(cisloJednaci.keyword:{{ string.replace d.usneseniCislo "/" "%2F" }})+OR+(usneseni.keyword:{{ string.replace d.usneseniCislo "/" "%2F20" }})">
          {{ d.usneseni }}</a>
      {{ end }}
     </li>

  {{ end }}
  </ul>
  </td></tr>
{{ end }}

{{ if item.veklep.size > 0 }}
  <tr>
  <td style="font-weight:bold;">Legislativní dokumenty</td>
  <td>
      {{ for v in item.veklep }}  
           <a href="https://apps.odok.cz/veklep-detail?pid={{v}}"  target=_blank>https://apps.odok.cz/veklep-detail?pid={{v}}</a> 
     {{ end }}
  </td>
  </tr>
{{ end }}
<tr>
<td style="font-weight:bold;">Zdroj jednání vlády</td>
<td><a href="https://apps.odok.cz/djv-agenda?date={{ fn_FormatDate item.datum 'yyyy-MM-dd' }}" target=_blank>https://apps.odok.cz/djv-agenda?date={{ fn_FormatDate item.datum 'yyyy-MM-dd' }}</a></td>
</tr>
{{ if (fn_IsNullOrEmpty item.usneseni)==false }}

  {{
  _rok = fn_GetRegexGroupValue item.usneseni "(?<cislo>\\d*) / (?<rok>\\d*)" "rok";
  _cislo = fn_GetRegexGroupValue item.usneseni "(?<cislo>\\d*) / (?<rok>\\d*)" "cislo";

  }}

  <tr>
  <td style="font-weight:bold;">Zdroj usnesení</td>
  <td><a href="https://apps.odok.cz/zvlady/usneseni/-/usn/{{ _rok }}/{{ _cislo }}" target=_blank>https://apps.odok.cz/zvlady/usneseni/-/usn/{{ _rok }}/{{ _cislo }}
  </a>

{{ end }}
</td>
</tr>

</table>

